int P547Solution::findCircleNum(vector<vector<int>>& M){
    vector<int> colors(M.size(), -1);
    int types = 0;
    for(int v_index=0;v_index<M.size();v_index++){
        for(int c_index=0; c_index<M[v_index].size();c_index++){
            if(c_index == v_index){
                continue;
            }
            if(M[v_index][c_index] == 1){
                if(colors[c_index] == -1 and colors[v_index] == -1){
                    colors[c_index] = types;
                    colors[v_index] = types;
                    types += 1;
                }
                int target_c = colors[c_index];
                if (target_c == -1)
                    target_c = colors[v_index];
                
                colors[c_index] = target_c;
                colors[v_index] = target_c;
                color_friends(M, c_index, target_c, colors);
                color_friends(M, v_index, target_c, colors);
            }
        }
    }
    
    set<int> res_set;
    int left_cnt = 0;
    for(auto &c:colors){
        if(c == -1)
            left_cnt += 1;
        else
            res_set.insert(c);
    }
    return res_set.size() + left_cnt;
}

void P547Solution::color_friends(vector<vector<int>> &M, int me, int color, vector<int> &colors){
    for(int j=0;j<M[me].size();j++){
        if(M[me][j] == 1 and colors[j] != color and j != me){
            colors[j] = color;
            color_friends(M, j, color, colors);
        }
    }
}

